options.css:
#myButton {
    display: inline-flex;
    -webkit-box-align: center;
    align-items: center;
    font-size: 18px;
    font-weight: 500;
    font-family: Roboto, Helvetica, Arial, sans-serif;
    padding: 0px 20px;
    height: 48px;
    line-height: 46px;
    letter-spacing: 0.01071em;
    vertical-align: middle;
    cursor: pointer;
    border-radius: 40px;
    background: rgb(163, 217, 217);
    color: rgb(255, 255, 255);
    border: 1px solid transparent;
    transition: background-color 350ms;
}

#myButton:hover {
    background: yellow;
    color: black;
}
#myButton:active {
  background-color: lightyellow;
  transform: translateY(2px);
}


#myButtonIcon {
    display: inline-block;
    vertical-align: middle;
    fill: currentcolor;
    margin-right: 8px;
    margin-left: -12px;
    overflow: hidden;

}

.myButtonText {
    display: flex;
    flex-direction: row;
    -webkit-box-align: center;
    align-items: center;
    white-space: nowrap;
    height: 100%;
}

div {
  padding: 10px;
}

input {
  width:180px;
  height:18px;
  padding:7px;
  border:1px solid black;
}

options.onInit:
// Sets the text from customProperties
const htmlgraphicsText = htmlNode.getElementById('htmlgraphics-text');

if (htmlgraphicsText) {
  htmlgraphicsText.textContent = customProperties.text;

  // Change the text color based on the theme
  if (theme.isDark) {
    htmlgraphicsText.style.color = 'green';
  } else {
    htmlgraphicsText.style.color = 'red';
  }
}

options.onRender:
var myButton = htmlNode.getElementById("myButton");
var myStartTime = parseInt(data.series[0].fields[0].values.buffer[0] / 1000);
var myStopTime = parseInt(data.series[0].fields[1].values.buffer[0] / 1000);
var samplingTime = getTemplateSrv().getVariables()[0].current.value

var queryBody = `
pH = from(bucket: "hydraulic-smart-controller")
  |> range(start: ${myStartTime}, stop: ${myStopTime})
  |> filter(fn: (r) => r["_measurement"] == "PH")
  |> filter(fn: (r) => r["_field"] == "Temperature" or r["_field"] == "PhValue")
  |> aggregateWindow(every: ${samplingTime}, fn: last, createEmpty: false)
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> drop(columns: ["sensor"])
  |> rename(columns: {Temperature: "Temperature_pH"})

density = from(bucket: "hydraulic-smart-controller")
  |> range(start: ${myStartTime}, stop: ${myStopTime})
  |> filter(fn: (r) => r["_measurement"] == "DENSITY")
  |> filter(fn: (r) => r["_field"] == "Density" or r["_field"] == "DynamicViscosity" or r["_field"] == "Temperature")
  |> filter(fn: (r) => r["sensor"] == "DENSITY")
  |> aggregateWindow(every: ${samplingTime}, fn: last, createEmpty: false)
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({
    r with kinematicViscosity: (r.DynamicViscosity/r.Density)*1000.0
  }))
  |> drop(columns: ["DynamicViscosity", "sensor"])
  |> rename(columns: {Temperature: "Temperature_density"})

conductivity = from(bucket: "hydraulic-smart-controller")
  |> range(start: ${myStartTime}, stop: ${myStopTime})
  |> filter(fn: (r) => r["_measurement"] == "CONDUCTIVITY")
  |> filter(fn: (r) => r["_field"] == "Salinity" or r["_field"] == "Tds" or r["_field"] == "Temperature" or r["_field"] == "ElectricalConductivity")
  |> aggregateWindow(every: ${samplingTime}, fn: last, createEmpty: false)
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> drop(columns: ["sensor"])
  |> rename(columns: {Temperature: "Temperature_conductivity"})

TempRelH = from(bucket: "hydraulic-smart-controller")
  |> range(start: ${myStartTime}, stop: ${myStopTime})
  |> filter(fn: (r) => r["_measurement"] == "T9501")
  |> filter(fn: (r) => r["_field"] == "Temperature" or r["_field"] == "RelativeHumidity")
  |> aggregateWindow(every: ${samplingTime}, fn: last, createEmpty: false)
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> drop(columns: ["sensor"])
  |> rename(columns: {Temperature: "Temperature_T9501"})

pH_density_join = join (
    tables: {pH: pH, density: density},
    on: ["_time", "_stop", "_start"]
)

conductivity_join = join (
    tables: {pH_density_join: pH_density_join, conductivity: conductivity},
    on: ["_time", "_stop", "_start"]
)

join (
    tables: {conductivity_join: conductivity_join, TempRelH: TempRelH},
    on: ["_time", "_stop", "_start"]
)
  |> drop(columns: ["_measurement_pH", "_measurement_density", "_measurement_conductivity_join", "_measurement_TempRelH"])
`;

